def senHebToArrayEng(sen):
    newSen = [switchHebToEng(tav) for tav in sen]
    for idx,tav in enumerate(newSen):
        if tav == None:
            newSen[idx] =' '
    strSen = "".join(newSen)
    newSen = strSen.split(' ')
    return newSen

def senEngToArrayHeb(sen):
    newSen = sen.split(" ")
    resultSen = []
    for word in newSen:                
        newWord = switchtavToSymbol(word)
        if newWord==None:
            newWord = [switchEngHeb(tav) for tav in word]                 
            if len(newWord)>0 and newWord[0] == None:
                newWord[0] =' '    
            else:                
                newWord = "".join(newWord)
        resultSen.append(newWord)    
    strSen = " ".join(resultSen)       
    newSen = strSen.split(' ')
    return newSen

